public function indexAction()\n{\n    // Fetch data from your database\n    $mainContentData = [\n        'title' => 'Welcome',\n        'description' => 'Your main content here.'\n    ];\n    \n    $headerContentData = ['page_name' => 'Home'];\n    $footerContentData = ['year' => date('Y')];\n    \n    // Pass data to the view\n    $this->view->mainContentData = $mainContentData;\n    $this->view->headerContentData = $headerContentData;\n    $this->view->footerContentData = $footerContentData;\n}