class ArticlesController extends AppController {\n    public $components = ['ContentChunker'];\n\n    public function view($id) {\n        $article = $this->Article->get($id);\n        $this->ContentChunker->chunkContent($article['content'], 1500); // chunk size example\n        \n        $chunks = $this->ContentChunker->getChunks();\n        \n        $this->set(compact('chunks'));\n    }\n}