function getBody($pid, PDO $pdo)\n{\n    $stmt = $pdo->prepare('SELECT * FROM cart_product WHERE product_id = :pid');\n    $stmt->execute(['pid' => (int)$pid]);\n    if ($row = $stmt->fetch()) {\n\n        $item_name = htmlspecialchars($row['product_name']);\n        $item_url = htmlspecialchars('product.php?pid='.$pid);\n        $item_image_url = htmlspecialchars('product_images/'.$pid.'_sm_'.$row['product_image_sm']);\n\n        return '<div style="width:600px; font-family:Arial, Helvetica, sans-serif; color:#333333;">'\n               . 'Your HTML content here using $item_name, $item_url, etc.'\n               . '</div>';\n    }\n    return '';\n}