<?php\n$pdo = new PDO('mysql:host=localhost;dbname=your_database', 'username', 'password');\n$input = $_GET['search']; // assuming the input is passed via GET\n\n$stmt = $pdo->prepare("SELECT * FROM locations WHERE place = :input OR postcode = :input");\n$stmt->bindParam(':input', $input, PDO::PARAM_STR);\n$stmt->execute();\n\n$results = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\nforeach ($results as $row) {\n    echo "Place: " . htmlspecialchars($row['place']) . ", Postcode: " . htmlspecialchars($row['postcode']) . "<br>";\n}\n?>