function getTemplateDirs($requestUrl) {\n    // Logic to determine which template directories to include based on $requestUrl\n    if (strpos($requestUrl, '/some-condition') !== false) {\n        return [\n            '/path/to/templates/condition1',\n            '/path/to/templates/folder2'\n        ];\n    }\n    return [\n        '/path/to/default/templates'\n    ];\n}\n\n// Example usage:\n$requestUrl = $_SERVER['REQUEST_URI'];\n$templateDirs = getTemplateDirs($requestUrl);\n$smarty->setTemplateDir($templateDirs);\n\n// Render the template\n$smarty->display('example.tpl');