<?php\n// Load the XML file\n$xml = new DOMDocument();\n$xml->load('example.xml');\n\n// Create a DOMXPath object to query XML\n$xpath = new DOMXPath($xml);\n\n// Find all custom 'textbox' elements\n$textboxes = $xpath->query('//body/textbox');\n\n// Iterate over each found element and perform operations (e.g., replace or manipulate)\nforeach ($textboxes as $textbox) {\n    // Example operation: replacing the textbox element with an input element\n    $input = $xml->createElement('input');\n    $input->setAttribute('type', 'text');\n    $input->setAttribute('name', $textbox->getAttribute('name'));\n    $textbox->parentNode->replaceChild($input, $textbox);\n}\n\n// Output or save the modified XML as HTML\necho $xml->saveHTML();\n?>