function removeValueFromArray($array, $valueToRemove) {\n    // Use array_filter to filter out the values we don't want\n    $filteredArray = array_filter($array, function($item) use ($valueToRemove) {\n        return $item !== $valueToRemove;\n    });\n\n    // Reindex the array using array_values\n    return array_values($filteredArray);\n}\n\n// Example usage:\n$myArray = [1, 2, 3, 4, 2, 5];\n$valueToRemove = 2;\n$resultArray = removeValueFromArray($myArray, $valueToRemove);\n\nprint_r($resultArray); // Output: Array ( [0] => 1 [1] => 3 [2] => 4 [3] => 5 )