class PostsController extends AppController {\n    var $name = 'Posts';\n\n    function add() {\n        if (!empty($this->request->data)) { // Updated for newer CakePHP versions\n            $this->Post->set($this->request->data); // Set data to model\n            if ($this->Post->validates()) { // Validate the data before saving\n                if ($this->Post->save($this->request->data)) {\n                    $this->Session->setFlash('Your post has been saved.');\n                    $this->redirect(array('action' => 'index'));\n                }\n            } else {\n                // Optionally handle validation errors and display them in the form.\n                $errors = $this->Post->validationErrors;\n                debug($errors); // Debug line to show validation errors\n                $this->Session->setFlash('Please check for errors');\n            }\n        }\n    }\n}