class Organization {\n    private $orgId;\n    private $name;\n    private $address;\n\n    public function __construct($orgId, $name) {\n        $this->orgId = $orgId;\n        $this->setName($name);\n        $this->address = null; // or some default value\n    }\n\n    public function getId() {\n        return $this->orgId;\n    }\n\n    public function getName() {\n        return $this->name;\n    }\n\n    public function setName($name) {\n        if (empty($name)) {\n            throw new InvalidArgumentException('Name cannot be empty');\n        }\n        $this->name = $name;\n    }\n\n    // Add logic related to organization here\n    public function isHeadquarteredIn($city) {\n        if ($this->address === null) {\n            return false;\n        }\n        return strtolower($this->address->getCity()) === strtolower($city);\n    }\n}\n\nclass Address {\n    private $street;\n    private $city;\n\n    // getters and setters\n}