<?php\nrequire_once('vendor/autoload.php');\n\nuse setasign\Fpdi\Tcpdf\Fpdi;\n\nfunction countPdfPages($file) {\n    $pdf = new Fpdi();\n    try {\n        // Set the source file\n        $pageCount = $pdf->setSourceFile($file);\n        return $pageCount;\n    } catch (Exception $e) {\n        echo 'Error: ' . $e->getMessage();\n        return false;\n    }\n}\n\n$file = 'example.pdf'; // Your PDF file path\necho "Number of pages in the PDF: " . countPdfPages($file);\n?>