$dbh = new PDO("mysql:host=$hostname;dbname=animals", $username, $password);\n\n// Set the default fetch mode to fetch rows into instances of 'Animal'\n$dbh->setAttribute(PDO::ATTR_DEFAULT_FETCH_MODE, PDO::FETCH_CLASS);\n$dbh->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION); // Recommended for development\n\n// Assuming class Animal has properties that map to column names\nclass Animal {\n    public $name;\n    // other properties...\n}\n\n$stmt = $dbh->query("SELECT * FROM animals");\n\nforeach ($stmt as $animal) {\n    echo $animal->name;\n}