<?php\n$q = "[code]<div>html code to display on screen</div>[/code]";\n\n// Regular expression pattern with s (dot matches newlines) and i (case insensitive) flags\n$pattern = '/\[code\](.*)\[\/code\]/si';\n\n// Perform preg_match and store results in $matches array\nif (preg_match($pattern, $q, $matches)) {\n    // Assuming you want the content between [code]...[/code]\n    $code_content = $matches[1];\n    \n    // Use htmlentities to prevent rendering HTML code\n    $safe_code_content = htmlspecialchars($code_content, ENT_QUOTES, 'UTF-8');\n    \n    echo "Code Content:\n";\n    echo "<pre>$safe_code_content</pre>";\n} else {\n    echo "No match found.";\n}\n?>