function deleteUsers($userIDs) {\n    global $conn; // Assuming you have a database connection stored in this variable\n\n    if (!is_array($userIDs)) {\n        throw new InvalidArgumentException("User IDs must be an array.");\n    }\n\n    // Prepare placeholders for each user ID\n    $placeholders = implode(',', array_fill(0, count($userIDs), '?'));\n    $query = "DELETE FROM members WHERE member_id IN ($placeholders)";\n\n    // Use prepared statements to prevent SQL injection\n    $stmt = $conn->prepare($query);\n    \n    if (!$stmt) {\n        throw new Exception("Error preparing statement: " . $conn->error);\n    }\n\n    // Bind parameters and execute statement\n    foreach ($userIDs as $key => &$id) {\n        $stmt->bind_param('i', $id); // Assuming member_id is an integer\n    }\n    \n    if (!$stmt->execute()) {\n        return 'no'; // Handle error, e.g., log it or return false\n    }\n\n    return 'yes';\n}