<?php\nfunction getLatestFacebookStatus($accessToken) {\n    // Endpoint URL for fetching posts - replace 'me' with your user ID if necessary\n    $graphApiUrl = "https://graph.facebook.com/me/feed?fields=status_type,message&access_token=" . urlencode($accessToken);\n\n    try {\n        // Initialize a cURL session\n        $ch = curl_init();\n        curl_setopt($ch, CURLOPT_URL, $graphApiUrl);\n        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false); // For production, ensure this is true and certificate is verified\n\n        // Execute the cURL request\n        $response = curl_exec($ch);\n\n        if (curl_errno($ch)) {\n            throw new Exception('cURL error: ' . curl_error($ch));\n        }\n\n        curl_close($ch);\n\n        // Decode JSON response\n        $data = json_decode($response, true);\n        \n        // Check for errors in the API response\n        if (isset($data['error'])) {\n            throw new Exception("API Error: " . $data['error']['message']);\n        }\n\n        // Get the latest status message if available\n        foreach ($data['data'] as $post) {\n            if ($post['status_type'] === 'added_photos') continue; // Skip photo uploads, for example\n\n            return $post['message'];\n        }\n    } catch (Exception $e) {\n        echo "Error: " . $e->getMessage();\n    }\n\n    return null;\n}\n\n// Usage - replace with your actual access token\n$accessToken = 'your_access_token_here';\n$status = getLatestFacebookStatus($accessToken);\nif ($status) {\n    echo "Latest Status: " . htmlspecialchars($status);\n} else {\n    echo "No recent status updates found or error occurred.";\n}\n?>