$lol = "hello";\n$that = "* music\n* books\n* games\n* asos\n* cry";\n\n// Replace '*' with the value of $lol and separate lines properly\n$lol_replaced = str_replace("*", $lol, $that);\n\n// Normalize newline characters to \r\n for better compatibility with Windows Notepad\n$lol_replaced = str_replace("\n", "\r\n", $lol_replaced);\n\n$name = str_replace(" ", "-", $lol);\n$myFile = "output/$name.csv";\n$fh = fopen($myFile, 'w') or die("can't open file");\n\nfwrite($fh, $lol_replaced);\nfclose($fh);