function advancedSearch($keyword, $findType) {\n    global $pdo; // Assume you have already set up a PDO connection\n\n    $keywords = explode(' ', trim($keyword));\n    $likeClauses = [];\n    $notLikeClauses = [];\n\n    foreach ($keywords as $word) {\n        if (!empty($word)) {\n            $likeClauses[] = "field LIKE :word";\n            $notLikeClauses[] = "field NOT LIKE :word";\n        }\n    }\n\n    $query = "SELECT * FROM myTable WHERE ";\n    \n    switch ($findType) {\n        case 'all':\n            // For exact match (all words)\n            foreach ($likeClauses as &$clause) {\n                $clause .= " AND"; // Change spaces to AND for all words\n            }\n            unset($clause); // Remove reference\n            $query .= implode(' ', $likeClauses);\n            break;\n        case 'at_least_one':\n            // For at least one word\n            $query .= implode(' OR ', $likeClauses);\n            break;\n        case 'without':\n            // For without words\n            $query .= implode(' AND ', $notLikeClauses);\n            break;\n        default:\n            return [];\n    }\n\n    // Trim trailing OR/AND if any and create a prepared statement\n    $preparedQuery = rtrim($query, " OR ");\n    $preparedQuery = rtrim($preparedQuery, " AND ");\n\n    $stmt = $pdo->prepare($preparedQuery);\n\n    // Bind parameters to avoid SQL injection\n    foreach ($keywords as $index => $word) {\n        $paramName = ':word';\n        $stmt->bindValue($paramName, '%' . addcslashes($word, '%_') . '%', PDO::PARAM_STR);\n    }\n\n    $stmt->execute();\n    return $stmt->fetchAll(PDO::FETCH_ASSOC);\n}\n\n// Usage\n$keyword = "I like apples";\n$resultsAll = advancedSearch($keyword, 'all');\n$resultsAtLeastOne = advancedSearch($keyword, 'at_least_one');\n$resultsWithout = advancedSearch($keyword, 'without');\n\nprint_r($resultsAll);\nprint_r($resultsAtLeastOne);\nprint_r($resultsWithout);