require_once 'Zend/Pdf.php';\n\n// Create a new PDF document\n$pdf = new Zend_Pdf();\n\n// Add a page\n$page = $pdf->newPage(Zend_Pdf_Page::SIZE_A4);\n$pdf->pages[] = $page;\n\n// Drawing a rectangle (shape)\n$page->setFillColor(new Zend_Pdf_Color_Rgb(1, 0, 0)) // Red fill color\n     ->drawRectangle(150, 450, 350, 650); // Draw rectangle\n\n// Setting the text font and size\n$page->setFont(Zend_Pdf_Font::fontWithName(Zend_Pdf_Font::FONT_HELVETICA), 18);\n\n// Drawing text over the rectangle\n$page->setFillColor(new Zend_Pdf_Color_Rgb(0, 0, 0)) // Black for the text color\n     ->drawText('This is a test', 200, 550); // Draw text\n\n// Save the PDF to a file or output it directly\n$pdf->save('/path/to/new.pdf');