<?php\n$host = 'localhost';\n$dbname = 'your_database_name';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    // Create PDO instance for MySQL database connection\n    $pdo = new PDO("mysql:host=$host;dbname=$dbname", $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Specify the table name and columns you are interested in\n    $tableName = 'your_table_name';\n    $columnName = 'your_varchar_column_name';\n\n    // SQL query to retrieve column length from INFORMATION_SCHEMA.COLUMNS\n    $query = "\n        SELECT CHARACTER_MAXIMUM_LENGTH\n        FROM INFORMATION_SCHEMA.COLUMNS\n        WHERE TABLE_NAME = :table AND COLUMN_NAME = :column\n        LIMIT 1;\n    ";\n\n    $stmt = $pdo->prepare($query);\n    $stmt->execute([':table' => $tableName, ':column' => $columnName]);\n\n    // Fetch the result\n    $result = $stmt->fetch(PDO::FETCH_ASSOC);\n\n    if ($result && isset($result['CHARACTER_MAXIMUM_LENGTH'])) {\n        $maxLength = $result['CHARACTER_MAXIMUM_LENGTH'];\n        echo "The maximum length of column '$columnName' is: $maxLength";\n    } else {\n        echo "Column not found or not a VARCHAR type.";\n    }\n\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n?>