<?php\n$mysqli = new mysqli("localhost", "user", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Assume $userId is the ID received from Flash, properly sanitized/validated\n$userId = intval($_POST['id']); // Ensure it's an integer\n\n$stmt = $mysqli->prepare("SELECT * FROM members WHERE id = ?");\n$stmt->bind_param("i", $userId);\n\nif ($stmt->execute()) {\n    $result = $stmt->get_result();\n    if ($row = $result->fetch_assoc()) {\n        echo json_encode($row); // Return user data as JSON\n    } else {\n        echo "No such user.";\n    }\n} else {\n    echo "Error: " . $stmt->error;\n}\n\n$stmt->close();\n$mysqli->close();\n?>