try {\n    // Create a new PDO instance\n    $pdo = new PDO("mysql:host=your_host;dbname=your_dbname", 'username', 'password');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Prepare the SQL statement\n    $stmt = $pdo->prepare('INSERT INTO dacc_data (top_level_category, sub_level_category, agent_name, date, ticket_id, company) VALUES (:top_level_category, :sub_level_category, :agent_name, NOW(), :ticket_id, :company)');\n\n    // Bind parameters to the statement\n    $stmt->bindParam(':top_level_category', $_POST['top_level_category']);\n    $stmt->bindParam(':sub_level_category', $_POST['sub_level_category']);\n    $stmt->bindParam(':agent_name', $_POST['agent_name']);\n    $stmt->bindParam(':ticket_id', $_POST['ticket_id']);\n    $stmt->bindParam(':company', $_POST['company']);\n\n    // Execute the statement\n    $stmt->execute();\n} catch (PDOException $e) {\n    die('Could not connect to the database or execute query: ' . $e->getMessage());\n}