function encryptData($string, $key) {\n    // Generate a random Initialization Vector (IV)\n    $iv = openssl_random_pseudo_bytes(openssl_cipher_iv_length('aes-256-cbc'));\n    \n    // Encrypt the data\n    $encryptedString = openssl_encrypt($string, 'aes-256-cbc', hash('sha256', $key), OPENSSL_RAW_DATA, $iv);\n    \n    // Store both the IV and encrypted string (e.g., concatenated with a separator)\n    return base64_encode($iv . '::' . $encryptedString);\n}\n\nfunction decryptData($encryptedString, $key) {\n    // Decode the data\n    list($iv, $encryptedData) = explode('::', base64_decode($encryptedString), 2);\n\n    // Decrypt the data\n    return openssl_decrypt($encryptedData, 'aes-256-cbc', hash('sha256', $key), OPENSSL_RAW_DATA, $iv);\n}\n\n// Example usage\n$key = "your_secret_key";\n$id = "concatenated_id_123";\n\n$encodedId = encryptData($id, $key);\necho "Encrypted ID: " . $encodedId . "\n";\n\n$decodedId = decryptData($encodedId, $key);\necho "Decrypted ID: " . $decodedId . "\n";