<?php\n$queryCount = 0;\n\nfunction performQuery($conn, $query) {\n    global $queryCount;\n    $result = mysqli_query($conn, $query);\n    if ($result !== FALSE) {\n        $queryCount++;\n    }\n    return $result;\n}\n\n// Example usage:\n$conn = mysqli_connect("localhost", "user", "password", "database");\n\n// Execute some queries\nperformQuery($conn, "SELECT * FROM users");\nperformQuery($conn, "INSERT INTO logs (message) VALUES ('Page loaded')");\n\necho "Number of SQL queries: " . $queryCount;\n\n?>