class MyTable extends BaseMyTable\n{\n    protected $fulltextColumnTsQuery;\n\n    public function setFulltextColumn($value, $searchText = null)\n    {\n        // Store the value normally\n        $this->setFulltextColumnValue($value);\n\n        if ($searchText) {\n            // Store the additional ts_query for custom logic\n            $this->fulltextColumnTsQuery = $searchText;\n        }\n    }\n\n    public function save(PropelPDO $con = null)\n    {\n        if (null !== $this->fulltextColumnTsQuery) {\n            // Custom SQL insert with PostgreSQL function\n            $sql = "INSERT INTO my_table (fulltext_column) VALUES (:value @@ to_tsquery('english', :searchText))";\n            $stmt = $con->prepare($sql);\n            $stmt->bindValue(':value', $this->getFulltextColumnValue());\n            $stmt->bindValue(':searchText', $this->fulltextColumnTsQuery);\n            $stmt->execute();\n        } else {\n            // Fallback to default save method\n            parent::save($con);\n        }\n    }\n\n    public function getFulltextColumnValue()\n    {\n        return $this->asColumn(MyTablePeer::FULLTEXT_COLUMN);\n    }\n\n    protected function setFulltextColumnValue($value)\n    {\n        $this->setByPosition($value, MyTablePeer::NUM_COLUMNS - MyTablePeer::FULLTEXT_COLUMN);\n    }\n}