<?php\n\nclass MyObject {\n    // Define flag constants\n    const FLAG_A = 1;       // 00001\n    const FLAG_B = 2;       // 00010\n    const FLAG_C = 4;       // 00100\n    const FLAG_D = 8;       // 01000\n    const FLAG_E = 16;      // 10000\n    const FLAG_F = 32;\n    const FLAG_G = 64;\n    const FLAG_H = 128;\n\n    private $flags;\n\n    public function __construct($flags = 0) {\n        $this->flags = (int)$flags;\n    }\n\n    // Method to get a flag\n    public function getFlag($flag) {\n        return ($this->flags & $flag) === $flag;\n    }\n\n    // Method to set a flag\n    public function setFlag($flag) {\n        $this->flags |= $flag;\n    }\n\n    // Method to unset a flag\n    public function unsetFlag($flag) {\n        $this->flags &= ~$flag;\n    }\n\n    // Get the raw flags value for database storage or debugging\n    public function getRawFlags() {\n        return $this->flags;\n    }\n}\n\n// Example usage:\n$obj = new MyObject();\n\n// Set some flags\n$obj->setFlag(MyObject::FLAG_A);\n$obj->setFlag(MyObject::FLAG_C);\n\n// Check if a flag is set\nvar_dump($obj->getFlag(MyObject::FLAG_A)); // bool(true)\nvar_dump($obj->getFlag(MyObject::FLAG_B)); // bool(false)\n\n// Save the raw flags to database\n$flagsToSave = $obj->getRawFlags();\n\n?>