<?php\n$host = 'your_host';\n$port = 1521;\n$sid = 'your_sid';\n$db = "(DESCRIPTION=(ADDRESS_LIST=(ADDRESS=(PROTOCOL=TCP)(HOST=$host)(PORT=$port)))\n           (CONNECT_DATA=(SID=$sid)))";\n$username = 'your_username';\n$password = 'your_password';\n\n// Set a shorter default timeout\nini_set('default_socket_timeout', 2); // Timeout in seconds\n\n$conn = @oci_pconnect($username, $password, $db);\n\nif (!$conn) {\n    // Handle error if connection fails\n    $e = oci_error();\n    echo "Connection failed: " . htmlentities($e['message'], ENT_QUOTES);\n} else {\n    echo "Successfully connected!";\n}\n?>