function base64DecodeToFile($encodedData, $outputFile) {\n    $decodedFile = fopen($outputFile, 'wb');\n    if (!$decodedFile) {\n        throw new Exception("Cannot open output file for writing");\n    }\n    \n    $inputLength = strlen($encodedData);\n    $bufferSize = 1024 * 16; // Read in chunks of 16KB\n    \n    for ($i = 0; $i < $inputLength; $i += $bufferSize) {\n        // Get the current chunk\n        $chunk = substr($encodedData, $i, $bufferSize);\n        \n        // Decode and write the chunk to the file\n        fwrite($decodedFile, base64_decode($chunk));\n    }\n    \n    fclose($decodedFile);\n}\n\n// Assuming $base64String is your encoded data received via API\ntry {\n    base64DecodeToFile($base64String, '/path/to/output/file.ext');\n} catch (Exception $e) {\n    echo 'Error: '. $e->getMessage();\n}