function CatRemove($myXML) {\n    $xmlDoc = new DOMDocument();\n    $xmlDoc->load($myXML);\n\n    // Get all 'category' elements\n    $categories = $xmlDoc->getElementsByTagName('category');\n\n    // Loop through the 'category' elements\n    foreach ($categories as $category) {\n        // Check if the current category has the matching ID\n        if ($category->getAttribute('id') == $_POST['categorytoremoveValue']) {\n            // Remove the node from its parent node\n            $category->parentNode->removeChild($category);\n            break; // Exit the loop after removing the node\n        }\n    }\n\n    // Save the modified XML back to the file\n    $xmlDoc->save($myXML);\n}