// Assuming $response is already decoded from JSON string and is an associative array\n$response = json_decode('{"name":"\u00fe\u00fd\u00f0\u00f6\u00e7"}', true);\n\n// Convert the 'name' part to ISO-8859-9\n$response['name'] = mb_convert_encoding($response['name'], "ISO-8859-9", "UTF-8");\n\n// Set header and re-encode array to JSON\nheader('Content-type: application/json; charset=ISO-8859-9');\n$json = json_encode($response);\n\necho $json;