<?php\n$keyArray = ["txt1", "txt2", "txt3"];\n$contentArray = [0 => "on", 2 => "on"];\n\n// Initialize an empty result array\n$resultArray = [];\n\n// Loop through the keys of the contentArray to match with the keyArray\nforeach ($contentArray as $key => $value) {\n    if (isset($keyArray[$key])) {\n        $resultArray[] = $keyArray[$key];\n    }\n}\n\n// Output the resulting array\nprint_r($resultArray);\n?>