<?php\n// Check the current user running the script\necho "Current User: " . get_current_user() . "\n";\n\n// Attempt to write to a test file\n$file = 'testfile.txt';\nif (file_put_contents($file, 'Test content')) {\n    echo "File was written successfully.\n";\n} else {\n    echo "Failed to write to the file.\n";\n}\n\n// Display ownership and permissions of the created file\nif (file_exists($file)) {\n    $fileOwner = fileowner($file);\n    $fileGroup = filegroup($file);\n    printf("File owner: %s\n", posix_getpwuid($fileOwner)['name']);\n    printf("File group: %s\n", posix_getgrgid($fileGroup)['name']);\n    echo "File permissions: " . substr(sprintf('%o', fileperms($file)), -4) . "\n";\n}\n?>