// Assuming $mysqli is your mysqli connection object\n$query = "INSERT INTO my_table (column1, column2) VALUES ('value1', 'value2')";\nif ($mysqli->query($query)) {\n    // Get the last insert ID for the same connection\n    $lastInsertId = $mysqli->insert_id;\n    echo "Last inserted ID is: " . $lastInsertId;\n\n    // You can now use $lastInsertId to perform another query\n    $anotherQuery = "INSERT INTO another_table (my_table_id, column3) VALUES ($lastInsertId, 'value3')";\n    if (!$mysqli->query($anotherQuery)) {\n        echo "Error in second insert: " . $mysqli->error;\n    }\n} else {\n    echo "Error in first insert: " . $mysqli->error;\n}