$validators = array(\n    'salutation' => array(\n        new Zend_Validate_NotEmpty(Zend_Validate_NotEmpty::STRING),\n        Zend_Filter_Input::MESSAGES => array(\n            Zend_Validate_NotEmpty::IS_EMPTY => "Please enter a salutation"\n        ),\n        Zend_Filter_Input::ALLOW_EMPTY => false,\n        Zend_Filter_Input::PRESENCE => Zend_Filter_Input::REQUIRED\n    ),\n    // Add other fields with similar configurations\n);\n\n$filters = array(); // Define filters as necessary\n\n$input = new Zend_Filter_Input($filters, $validators, $_POST);\nif (!$input->isValid()) {\n    $messages = $input->getMessages();\n    print_r($messages); // This will now show your custom messages for empty fields\n}