<?php\n\nfunction convertMoneyPattern($matches) {\n    $value = $matches[1];\n    $suffix = $matches[2];\n    \n    // Convert the value to float\n    $number = floatval($value);\n    \n    if ($suffix === 'M') {\n        return strval($number * 1000000);\n    } elseif ($suffix === 'T') {\n        return strval($number * 10000);\n    }\n    return $matches[0]; // In case of an unexpected suffix, return the original string\n}\n\n$input = "Here are some values: 15M, 15.3M, 15T or 15.3T.";\n$output = preg_replace_callback('/(\d+(\.\d+)?)([MT])/', 'convertMoneyPattern', $input);\n\necho $output;\n\n?>