<?php\n$directory = '/path/to/your/folder'; // Replace with your folder path\n\n// Use scandir() to get an array of file names in the directory.\n$files = scandir($directory);\n\n// Remove '.' and '..' from the list which represent current and parent directories\n$files = array_diff($files, ['.', '..']);\n\necho "<h2>Files in Folder:</h2><ul>";\nforeach ($files as $file) {\n    echo "<li>$file</li>";\n}\necho "</ul>";\n?>