// Assuming $pdo is your PDO connection object\n$query = "INSERT INTO my_table (column1, column2) VALUES ('value1', 'value2')";\n$statement = $pdo->prepare($query);\nif ($statement->execute()) {\n    // Get the last insert ID for the same connection\n    $lastInsertId = $pdo->lastInsertId();\n    echo "Last inserted ID is: " . $lastInsertId;\n\n    // Use $lastInsertId as needed in another query\n} else {\n    echo 'Error in insert: ' . implode(', ', $statement->errorInfo());\n}