if ($handle = opendir($absolute_dir)) {\n    $image_array = array();\n\n    while (($file = readdir($handle)) !== false) {\n        if ($file != "." && $file != ".." && $file != ".svn" && $file != 'img') {\n            $image_info['url'] = $relative_dir . $file;\n            $image_info['last_modified_time'] = filemtime($absolute_dir . '/' . $file); // Use the timestamp for sorting\n            $image_array[] = $image_info; // Add each file to the array with its info\n        }\n    }\n    closedir($handle);\n\n    // Sort the images by last modified time, newest first\n    usort($image_array, function ($a, $b) {\n        return $b['last_modified_time'] - $a['last_modified_time'];\n    });\n\n    // Limit to the 5 newest files\n    $image_array = array_slice($image_array, 0, 5);\n}