<?PHP\n    error_reporting(E_ALL);\n    $src = "/var/videos/video1.wmv";\n    $command = "/usr/bin/ffmpeg -i " . escapeshellarg($src) . " 2>&1";\n    \n    echo "<B>Command:</B> ", htmlspecialchars($command), "<br/>\n";\n\n    // Using shell_exec to capture both stdout and stderr\n    echo "shell_exec:<br/><pre>";\n    $output = shell_exec($command);\n    echo htmlspecialchars($output ?: "No output");\n    echo "</pre><br/>\n";\n\n    // Using exec to capture both stdout and stderr\n    echo "exec:<br/><pre>";\n    $outputArray = [];\n    exec($command, $outputArray, $status);\n    foreach ($outputArray as $line) {\n        echo htmlspecialchars($line), "<br/>\n";\n    }\n    echo "</pre><br/>\n";\n\n    // Using passthru to capture both stdout and stderr\n    ob_start();\n    passthru($command);\n    $output = ob_get_clean();\n    echo "passthru:<br/><pre>";\n    echo htmlspecialchars($output ?: "No output");\n    echo "</pre><br/>\n";\n?>