<?php\n$filename = 'counter.txt';\n\nfunction incrementCounter($filename) {\n    // Open the file for reading and writing\n    $fileHandle = fopen($filename, 'c+');\n    \n    if ($fileHandle === false) {\n        throw new Exception("Could not open file.");\n    }\n\n    try {\n        // Lock the file for exclusive access\n        if (flock($fileHandle, LOCK_EX)) {\n            // Read the counter value\n            $counter = intval(trim(fread($fileHandle, filesize($filename))));\n            \n            // Increment the counter\n            $counter++;\n            \n            // Rewind to the beginning of the file to overwrite\n            rewind($fileHandle);\n            \n            // Write the new counter value back to the file\n            fwrite($fileHandle, $counter);\n            \n            // Explicitly release the lock (optional as it will be released when the filehandle is closed)\n            flock($fileHandle, LOCK_UN);\n        } else {\n            throw new Exception("Could not acquire lock.");\n        }\n    } finally {\n        // Close the file handle\n        fclose($fileHandle);\n    }\n}\n\n// Increment and display the counter\nincrementCounter($filename);\n\necho "Page Load Count: " . intval(file_get_contents($filename));\n?>