class DataImportExecutor {\n    private $getter;\n    private $handler;\n    private $writer;\n\n    public function __construct(\n        DataGetterInterface $getter, \n        DataHandlerInterface $handler, \n        DataWriterInterface $writer\n    ) {\n        $this->getter = $getter;\n        $this->handler = $handler;\n        $this->writer = $writer;\n    }\n\n    public function execute() {\n        try {\n            $data = $this->getter->getData();\n            $processedData = $this->handler->handle($data);\n            $this->writer->write($processedData);\n        } catch (\Exception $e) {\n            // Handle exceptions\n        }\n    }\n}