// The definition of what to call\n$function_call_spec = array( \n    "prototype"  => "myFunction", \n    "parameters" => array( \n        "first_par"  => "Hello",\n        "second_par" => "World"\n    )\n);\n\n// Define the function with clear arguments\nfunction myFunction($first_par, $second_par) {\n    echo "$first_par $second_par";\n}\n\n// Dispatch using call_user_func_array\n$funcPrototype = $function_call_spec["prototype"];\ncall_user_func_array($funcPrototype, array_values($function_call_spec["parameters"]));