// In status.php\nsession_start();\n\n$server2 = explode(":", $xfr[3]); // Assuming $xfr is defined somewhere in your global scope\n$socket2 = pfsockopen($server2[0], (int)$server2[1]);\n\nif ($socket2) {\n    echo '<b>Connected to server.</b><br />';\n\n    // Send status change command (make sure $_GET['s'] is sanitized!)\n    fputs($socket2, "CHG 12 " . htmlspecialchars($_GET["s"]) . " " . $_SESSION["cid"] . "\r\n");\n\n    // Read response\n    $response = fread($socket2, 5000);\n    echo $response;\n\n    // Close the socket after use\n    fclose($socket2);\n} else {\n    echo '<b>Failed to connect to server.</b><br />';\n}