<?php\n// Disable cookies for sessions by setting them to false\nsession_set_cookie_params([\n    'lifetime' => 0,\n    'path' => '/',\n    'domain' => $_SERVER['HTTP_HOST'],\n    'secure' => true,\n    'httponly' => true,\n    'samesite' => 'Strict',\n]);\n\n// Disable using cookies entirely\nini_set('session.use_cookies', 0);\nini_set('session.use_only_cookies', 1);\n\n// Start the session\nsession_start();\n\n// Now you can manage your session ID manually, e.g., via POST or URL parameters\n?>