// Assuming $mailQueue is an instance of Mail_Queue\n$user_id = 1;\n$time_to_send = '2009-02-10 14:02:02';\n\n// Check if entry already exists\n$resultSet = $mailQueue->get(null, ['user_id', 'time_to_send'], [$user_id, $time_to_send]);\nif ($resultSet->numRows() == 0) {\n    // Insert new entry only if it doesn't exist\n    $mailQueue->put('subject', 'message body', 'recipient@example.com', null, $time_to_send, $user_id);\n}