<?php\n$uploadDirectory = './uploads/';\n$maxFileSize = 5000000;\n\nif ($_SERVER['REQUEST_METHOD'] == 'POST') {\n    if (isset($_FILES['recording']) && $_FILES['recording']['error'] == UPLOAD_ERR_OK) {\n        $fileTmpPath = $_FILES['recording']['tmp_name'];\n        $fileName = basename($_FILES['recording']['name']);\n        $destinationPath = $uploadDirectory . $fileName;\n\n        if ($_FILES['recording']['size'] > $maxFileSize) {\n            die(json_encode(['error' => 'File size is too large']));\n        }\n\n        if (move_uploaded_file($fileTmpPath, $destinationPath)) {\n            echo json_encode(['message' => 'Video uploaded successfully']);\n        } else {\n            echo json_encode(['error' => 'Failed to move uploaded file']);\n        }\n    } else {\n        echo json_encode(['error' => 'There was an error uploading your video']);\n    }\n}\n?>