class Database {\n    private $pdo;\n\n    public function __construct($dsn, $username, $password) {\n        $this->pdo = new PDO($dsn, $username, $password);\n    }\n\n    public function insertData($data) {\n        // Prepare the SQL statement to prevent SQL injection\n        $stmt = $this->pdo->prepare("INSERT INTO table_name (column) VALUES (:value)");\n        \n        // Bind data to the prepared statement\n        $stmt->bindParam(':value', $data, PDO::PARAM_STR);\n        \n        // Execute the query\n        return $stmt->execute();\n    }\n\n    public function fetchData($id) {\n        $stmt = $this->pdo->prepare("SELECT column FROM table_name WHERE id = :id");\n        $stmt->bindParam(':id', $id, PDO::PARAM_INT);\n        $stmt->execute();\n        \n        // Fetch the data\n        return $stmt->fetch(PDO::FETCH_ASSOC);\n    }\n}\n\n// Usage example\n$db = new Database('mysql:host=localhost;dbname=testdb', 'username', 'password');\n$data = "Some user input";\n$db->insertData($data);\n\n$fetchedData = $db->fetchData(1);\necho htmlspecialchars($fetchedData['column']);