use MyCLabs\Enum\Enum;\n\nclass Status extends Enum\n{\n    private const ACTIVE = 'active';\n    private const INACTIVE = 'inactive';\n    private const PENDING = 'pending';\n\n    // No need for additional methods like toString or equalTo, these are provided by the library.\n}\n\n// Usage\n$status = Status::ACTIVE(); // Returns an instance of Status::ACTIVE\necho $status->getKey(); // Outputs: ACTIVE