<?php\n\n$dsn = 'mysql:host=your_host;dbname=your_db;charset=utf8mb4';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    $pdo = new PDO($dsn, $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Read the file in chunks if it's very large. For simplicity, reading all at once here.\n    $data = file_get_contents('large_file.bin');\n    $hash = md5($data);\n\n    $stmt = $pdo->prepare("INSERT INTO some_table (BlobData, BlobHash) VALUES (:blobData, :blobHash)");\n    \n    // Bind the data to the statement\n    $stmt->bindParam(':blobData', $data, PDO::PARAM_LOB);\n    $stmt->bindParam(':blobHash', $hash);\n\n    // Execute the query\n    $stmt->execute();\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n\n?>