class RoleManager {\n    private $roles;\n\n    public function __construct() {\n        // Define roles and their permissions\n        $this->roles = [\n            'admin' => ['create', 'read', 'update', 'delete'],\n            'editor' => ['read', 'update'],\n            'viewer' => ['read']\n        ];\n    }\n\n    public function can($role, $permission) {\n        return in_array($permission, $this->roles[$role] ?? []);\n    }\n}\n\n$rm = new RoleManager();\nif ($rm->can('editor', 'update')) {\n    echo "The editor can update posts.";\n} else {\n    echo "This role cannot perform this action.";\n}