$baseUrl = 'https://www.example.com'; // Use HTTPS for security\n$post_data = [\n    'user_login' => 'admin',\n    'user_pass' => 'password', // Ensure credentials are stored securely\n    'wp-submit' => 'Log In',\n    'redirect_to' => $baseUrl . '/wp-admin/',\n];\n\n$ch = curl_init();\n\ncurl_setopt($ch, CURLOPT_URL, $baseUrl . '/wp-login.php');\ncurl_setopt($ch, CURLOPT_POST, 1);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($post_data));\ncurl_setopt($ch, CURLOPT_COOKIEJAR, 'cookie.txt'); // Store cookies in a file\ncurl_setopt($ch, CURLOPT_COOKIEFILE, 'cookie.txt'); // Send cookies from the file\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false); // Only for testing; should be enabled in production\n\n$response = curl_exec($ch);\n\nif ($response === false) {\n    echo 'Curl error: ' . curl_error($ch);\n}\n\n$httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);\nif ($httpCode >= 200 && $httpCode < 400) {\n    // Check if login was successful\n    echo 'Login successful!';\n} else {\n    echo 'Failed to log in.';\n}\n\ncurl_close($ch);