<?php\n$mysqli = new mysqli("host", "user", "password", "database");\n\nif ($mysqli->connect_error) {\n    die('Connect Error (' . $mysqli->connect_errno . ') ' . $mysqli->connect_error);\n}\n\n$currentName = 'Dunn'; // The name of the current page\n\n// Query to get 3 links before and 3 links after the current record alphabetically\n$sql = "\nSELECT *\nFROM your_table_name\nWHERE name > ? OR name < ?\nORDER BY name ASC\nLIMIT 7;\n";\n\n$stmt = $mysqli->prepare($sql);\n$stmt->bind_param("ss", $currentName, $currentName);\n$stmt->execute();\n$result = $stmt->get_result();\n\n$names = [];\nwhile ($row = $result->fetch_assoc()) {\n    $names[] = $row['name'];\n}\n\n// Sort names to ensure alphabetical order\nsort($names);\n\n// Find the index of the current name\n$current_index = array_search($currentName, $names);\n\nif ($current_index !== false) {\n    // Define limits for links to display\n    $start = max(0, $current_index - 3);\n    $end = min(count($names), $current_index + 4);\n\n    echo '<ul>';\n    for ($i = $start; $i < $end; $i++) {\n        if ($names[$i] === $currentName) {\n            echo '<li><strong>' . htmlspecialchars($names[$i]) . '</strong></li>';\n        } else {\n            echo '<li>' . htmlspecialchars($names[$i]) . '</li>';\n        }\n    }\n    echo '</ul>';\n}\n\n$stmt->close();\n$mysqli->close();\n?>